function e(e){return e&&e.__esModule?e.default:e}var a;a=JSON.parse('{"lang__menu-link-home":{"ua":"Фонд \\"ЗАНОВО\\"","en":"Fund \\"RENOVATE\\""},"lang__menu-link-projects":{"ua":"Проєкти","en":"Projects"},"lang__menu-link-about":{"ua":"Про нас","en":"About us"},"lang__menu-link-report":{"ua":"Звіти і Подяки","en":"Reports and Thanks"},"lang__menu-link-contacts":{"ua":"Контакти","en":"Contacts"},"lang__menu-link-donate":{"ua":"Зробити донат","en":"Donation"},"lang__menu-sub-link-zsu-help":{"ua":"Допомога ЗСУ","en":"ZSU Help"},"lang__menu-sub-link-reconstruction":{"ua":"Відбудова країни","en":"Reconstruction"},"lang__menu-sub-link-nets":{"ua":"Загін СМС","en":"Squad of NCF"},"lang__menu-sub-link-candle":{"ua":"Сили ССО","en":"Powers of SCO"},"lang__menu-sub-link-humanitarian":{"ua":"Гуманітарна допомога","en":"Humanitarian help"},"lang__menu-sub-link-events":{"ua":"Благодійні заходи","en":"Charity Events"}}');var t;t=JSON.parse('{"lang__footer-address-title":{"ua":"Благодійний фонд \'\'ЗАНОВО\'\'","en":"Charity Fund \'\'RENOVATE\'\'"},"lang__footer-address-recvizit":{"ua":"ЄДРПОУ: 44659830","en":"ID TAX: 44659830"},"lang__footer-address-location":{"ua":"м. Київ, вул. Дегтярівська 5","en":"Kyiv, Degtyarivska St. 5"},"lang__footer-qr-title":{"ua":"Постійний збір","en":"Constant donation"},"lang__footer-docs-link-statute":{"ua":"Статут","en":"Statute"},"lang__footer-docs-link-extract":{"ua":"Витяг з реєстру","en":"Registry extract"},"lang__footer-docs-link-certificate":{"ua":"Довідка про неприбутковість","en":"Non-profit certificate"},"lang__footer-address-copyright":{"ua":"&#169; 2024 - Благодійний фонд \\"ЗАНОВО\\". <br class=\\"footer__br\\"> Всі права захищені.","en":"&#169; 2024 - Renovate Charity Foundation. <br class=\\"footer__br\\"> All rights reserved."}}');var n;n=JSON.parse('{"home-page-title":{"ua":"Благодійний фонд ЗАНОВО","en":"RENOVATE CHARITY"},"lang__hero":{"ua":"<span class=\\"hero__title-primary\\">с</span><span class=\\"hero__title-primary\\">в</span><span class=\\"hero__title-primary\\">о</span><span class=\\"hero__title-primary\\">є</span><span class=\\"hero__title-primary\\">&nbsp;</span><span class=\\"hero__title-accent\\">з</span><span class=\\"hero__title-accent\\">а</span><span class=\\"hero__title-accent\\">х</span><span class=\\"hero__title-accent\\">и</span><span class=\\"hero__title-accent\\">с</span><span class=\\"hero__title-accent\\">т</span><span class=\\"hero__title-accent\\">и</span><span class=\\"hero__title-accent\\">м</span><span class=\\"hero__title-accent\\">о</span><span class=\\"hero__title-accent\\">,</span><span class=\\"hero__title-primary\\"><br></span><span class=\\"hero__title-accent\\">в</span><span class=\\"hero__title-accent\\">і</span><span class=\\"hero__title-accent\\">д</span><span class=\\"hero__title-accent\\">б</span><span class=\\"hero__title-accent\\">у</span><span class=\\"hero__title-accent\\">д</span><span class=\\"hero__title-accent\\">у</span><span class=\\"hero__title-accent\\">є</span><span class=\\"hero__title-accent\\">м</span><span class=\\"hero__title-accent\\">о</span><span class=\\"hero__title-primary\\">&nbsp;</span><span class=\\"hero__title-primary\\">з</span><span class=\\"hero__title-primary\\">а</span><span class=\\"hero__title-primary\\">н</span><span class=\\"hero__title-primary\\">о</span><span class=\\"hero__title-primary\\">в</span><span class=\\"hero__title-primary\\">о</span><span class=\\"hero__title-primary\\">!</span>","en":"<span class=\\"hero__title-primary\\">o</span><span class=\\"hero__title-primary\\">u</span><span class=\\"hero__title-primary\\">r</span><span class=\\"hero__title-primary\\">s</span><span class=\\"hero__title-primary\\">&nbsp;</span><span class=\\"hero__title-accent\\">p</span><span class=\\"hero__title-accent\\">r</span><span class=\\"hero__title-accent\\">o</span><span class=\\"hero__title-accent\\">t</span><span class=\\"hero__title-accent\\">e</span><span class=\\"hero__title-accent\\">c</span><span class=\\"hero__title-accent\\">t</span><span class=\\"hero__title-accent\\">,</span><span class=\\"hero__title-primary\\"><br></span><span class=\\"hero__title-accent\\">r</span><span class=\\"hero__title-accent\\">e</span><span class=\\"hero__title-accent\\">b</span><span class=\\"hero__title-accent\\">i</span><span class=\\"hero__title-accent\\">l</span><span class=\\"hero__title-accent\\">d</span><span class=\\"hero__title-primary\\">&nbsp;</span><span class=\\"hero__title-primary\\">a</span><span class=\\"hero__title-primary\\">n</span><span class=\\"hero__title-primary\\">e</span><span class=\\"hero__title-primary\\">w</span><span class=\\"hero__title-primary\\">!</span>"},"lang__main-about-text-1":{"ua":"З лютого 2022 року ми почали надавати допомогу на Київському залізничному вокзалі. Наша команда з понад 100 волонтерів допомагала військовим, біженцям, соціальним центрам і лікарням. Завдяки вашим внескам ми забезпечували гуманітарні вантажі для регіонів, постраждалих від війни, а також надавали харчування сотням людей щодня.","en":"From February 2022, we started providing assistance at the Kyiv railway station. Our team of over 100 volunteers helped the military, refugees, social centers and hospitals. Thanks to your contributions, we have provided humanitarian supplies to war-torn regions and fed hundreds of people every day."},"lang__main-about-text-2":{"ua":"Труднощі нас лише зміцнювали, і сьогодні ми, об\'єднані в благодійний фонд \\"Заново\\", продовжуємо працювати за кількома напрямками: відбудова країни, гуманітарна допомога, підтримка ЗСУ, плетіння сіток, виготовлення свічок та організація благодійних заходів. Кожен внесок і кожна година праці наших волонтерів наближає нас до перемоги. Ми віримо у квітуче майбутнє України і разом працюємо задля його досягнення.","en":"Difficulties only strengthened us, and today we, united in the \\"RENOVATE\\" charitable foundation, continue to work in several directions: reconstruction of the country, humanitarian aid, support of the Armed Forces, weaving nets, making candles and organizing charity events. Every contribution and every hour of work of our volunteers brings us closer to victory. We believe in the prosperous future of Ukraine and work together to achieve it."},"lang__main-icons-title-1":{"ua":"Допомога ЗСУ","en":"Help to ZSU"},"lang__main-icons-description-1":{"ua":"Забезпечуємо наших військових необхідним обладнанням, спорядженням та медикаментами. Ми співпрацюємо з волонтерами та партнерами, щоб оперативно доставляти допомогу на передову, підтримуючи захисників у їх боротьбі за мир і безпеку.","en":"We provide our military with the necessary equipment, supplies and medicines. We work with volunteers and partners to quickly deliver aid to the frontlines, supporting defenders in their fight for peace and security."},"lang__main-icons-title-2":{"ua":"Відбудова країни","en":"Reconstruction country"},"lang__main-icons-description-2":{"ua":"Відновлюємо інфраструктуру, постраждалу від воєнних дій. Залучаємо ресурси та волонтерів для ремонту житлових будинків, шкіл і лікарень, щоб повернути громадам стабільність і комфортне життя.","en":"We are restoring infrastructure damaged by military operations. We are mobilizing resources and volunteers to repair residential buildings, schools and hospitals to restore stability and comfortable living to communities."},"lang__main-icons-title-3":{"ua":"Загін сітко-маскувальних сил","en":"Squad of Net-Camouflage Forces"},"lang__main-icons-description-3":{"ua":"Виготовляємо та постачаємо маскувальні сітки для військових підрозділів. Наші зусилля сприяють підвищенню захисту техніки та особового складу на передовій.","en":"We manufacture and supply camouflage nets for military units. Our efforts contribute to increasing the protection of equipment and personnel on the front lines."},"lang__main-icons-title-4":{"ua":"Сили спеціальних свічкових операцій","en":"Powers of special candle operations"},"lang__main-icons-description-4":{"ua":"Забезпечуємо військових засобами для світла та тепла під час бойових завдань. Наша підтримка сприяє підвищенню комфорту перебування у важких умовах.","en":"We provide military equipment for light and heat during combat missions. Our support helps increase the comfort of staying in difficult conditions."},"lang__main-icons-title-5":{"ua":"Гуманітарна допомога","en":"Humanitarian help"},"lang__main-icons-description-5":{"ua":"Надаємо гуманітарну допомогу постраждалим від воєнних дій. Забезпечуємо продуктами, одягом та необхідними речами, щоб підтримати людей у складних життєвих обставинах.","en":"We provide humanitarian assistance to victims of military operations. We provide food, clothing and necessary things to support people in difficult life circumstances."},"lang__main-icons-title-6":{"ua":"Благодійні заходи","en":"Charity events"},"lang__main-icons-description-6":{"ua":"Організовуємо благодійні заходи для збору коштів та підвищення обізнаності про наші проєкти. Залучаємо громаду до активної участі у благодійності та підтримці потребуючих.","en":"We organize charity events to raise funds and raise awareness of our projects. We involve the community in active participation in charity and support of the needy."}}');var s;s=JSON.parse('{"about-us-page-title":{"ua":"Благодійний фонд ЗАНОВО. Про нас","en":"RENOVATE CHARITY. About us."},"lang__about-us-title":{"ua":"Хто ми такі","en":"Who are we"},"lang__about-us-text-1":{"ua":"З чого ми починали (і чим продовжуємо займатися зараз)","en":"What we started with (and what we continue to do now)"},"lang__about-us-text-2":{"ua":"З 28 лютого ми почали займатися волонтерством на Київському з-д вокзалі.","en":"From February 28, we started volunteering at the Kyiv railway station."},"lang__about-us-text-3":{"ua":"З чисельністю більше 100 осіб, \\"Волонтерський Центр\\" надавав допомогу підрозділам і військовим частинам, іншим волонтерським організаціям, а також біженцям, соціальним центрам, будинкам догляду за літніми людьми, шпиталям і лікарням, бомбосховищам та місцевому населенню.","en":"With a staff of more than 100 people, the \\"Volunteer Center\\" provided assistance to units and military units, other volunteer organizations, as well as refugees, social centers, care homes for the elderly, hospitals and clinics, bomb shelters and the local population."},"lang__about-us-text-4":{"ua":"Наші волонтери сприяли у забезпеченні гуманітарними вантажами для мешканців Київського, Чернігівського, Сумського, Харківського, Полтавського, Донецького, Херсонського та Миколаївського регіонів. Кожним координатором і волонтером пророблена гігантська робота. Ми приймали, складували і спрямовували великі вантажі з потягів або, навпаки, завантажували гуманітарну допомогу до потягів різних напрямків.","en":"Our volunteers contributed to the provision of humanitarian goods for residents of the Kyiv, Chernihiv, Sumy, Kharkiv, Poltava, Donetsk, Kherson and Mykolaiv regions. Every coordinator and volunteer has done a gigantic job. We received, stored and sent large loads from trains or, on the contrary, loaded humanitarian aid onto trains of various directions."},"lang__about-us-text-5":{"ua":"А ще майже щодня отримували та розподіляли до 400 відер вареників та всіляких смаколиків із західних областей України. Тож біженці, військові та бійці ТрО Києва та його околиць ніколи не залишались голодними. А ще приймали евакуаційні потяги, автобуси та машини, що вивозили людей з окупованих міст та гарячих точок. Всі справи кінця лютого – початку квітня 2022 й не перелічити...","en":"And almost every day they received and distributed up to 400 buckets of dumplings and all kinds of goodies from the western regions of Ukraine. Therefore, refugees, military personnel and fighters of the Military District of Kyiv and its surroundings never remained hungry. They also received evacuation trains, buses and cars that took people out of occupied cities and hot spots. All the affairs of the end of February - beginning of April 2022 cannot be listed..."},"lang__about-us-text-6":{"ua":"То були важкі часи цілодобової безоглядної праці, але ми всі згуртувались коло однієї мети – допомогти захищати нашу Землю, вистояти та перемогти!","en":"Those were difficult times of round-the-clock careless work, but we all rallied around one goal – to help protect our Earth, to stand and win!"},"lang__about-us-text-7":{"ua":"Весь час ми хотіли робити все більше і більше задля допомоги й тим, хто нас захищає в ці страшні часи, й тим, хто постраждав в результаті агресії та потребує підтримки – так, координатори Волонтерів Київського Вокзалу стали засновниками благодійного фонду \\"Заново\\".","en":"All the time, we wanted to do more and more to help both those who protect us in these terrible times and those who suffered as a result of aggression and need support - yes, the coordinators of Volunteers of the Kyiv Railway Station became the founders of the charity fund \\"RENOVATE\\"."},"lang__about-us-text-8":{"ua":"<span class=\\"text-bold\\">\\"Заново\\" — це про Відновлення</span>. Це місце в якому одночасно зібралися люди різних професій: інженер-архітектор, дизайнер, режисер, фармацевт, лікар, палеонтолог, кухар, електрик, вантажник, менеджер, юрист, тощо. Всіх нас, таких різних, об’єднує величезне бажання наблизити нашу перемогу та допомогти нашій країні відновлюватись! Кожного дня ми продовжуємо йти за покликом душі з однією лиш метою – допомогти країні вистояти.","en":"<span class=\\"text-bold\\">\\"RENOVATE\\" is about Restoration</span>. This is a place where people of different professions gathered at the same time: engineer-architect, designer, director, pharmacist, doctor, paleontologist, cook, electrician, porter, manager, lawyer, etc. All of us, so different, are united by a huge desire to bring our victory closer and help our country recover! Every day, we continue to follow our soul\'s calling with only one goal - to help the country survive."},"lang__about-us-text-9":{"ua":"На сьогодні наш фонд працює за такими напрямками: <a class=\\"about__project-link text-bold link\\" href=\\"./unit-reconstruction.html\\" aria-label=\\"Charity page\\">відбудова країни</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-humanitarian-help.html\\" aria-label=\\"Humanitarian help page\\">гуманітарна допомога</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-zsu-help.html\\" aria-label=\\"ZSU help page\\">допомога ЗСУ</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-nets.html\\" aria-label=\\"Nets page\\">плетіння сіток</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-candle.html\\" aria-label=\\"Candle page\\">виготовлення окопних свічок</a> та <a class=\\"about__project-link text-bold link\\" href=\\"./unit-events.html\\" aria-label=\\"Events page\\">творчі благодійні заходи</a>. Кожен з цих напрямків об’єднує прекрасних людей, які безупинно працюють й вирішують неможливі задачі з пошуку необхідного для досягнення наших цілей. Всі ми віримо <span class=\\"text-uppercase\\">в нашу перемогу, в країну</span>, в якій живемо! Віримо в те, що <span class=\\"text-uppercase\\">квітуче майбутнє україни – це реальність</span>, адже стільки людей об’єднані цією єдиною метою, а тому <span class=\\"text-uppercase\\">разом ми переможемо</span>!","en":"Today, our fund works in the following directions: <a class=\\"about__project-link text-bold link\\" href=\\"./unit-reconstruction.html\\" aria-label=\\"Charity page\\">reconstruction countries</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-humanitarian-help.html\\" aria-label=\\"Humanitarian help page\\">humanitarian help </a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-zsu-help.html\\" aria-label=\\"ZSU help page\\">ZSU help< /a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-nets.html\\" aria-label=\\"Nets page\\">weaving nets</a>, <a class=\\"about__project-link text-bold link\\" href=\\"./unit-candle.html\\" aria-label=\\"Candle page\\">making trench candles</a> and <a class =\\"about__project-link text-bold link\\" href=\\"./unit-events.html\\" aria-label=\\"Events page\\">creative charity events</a>. unites wonderful people who work tirelessly and solve impossible tasks in search of what is necessary to achieve our goals. We all believe <span class=\\"text-uppercase\\">in our victory, in the country</span> in which we live! We believe that <span class=\\"text-uppercase\\">the flourishing future of Ukraine is a reality</span>, because so many people are united by this one goal, and therefore <span class=\\"text-uppercase\\">together we will win</span>!"}}');var i;i=JSON.parse('{"lang__reports-title":{"ua":"Звіти БФ \\"Заново\\"","en":"Reports of Charity Fund \\"Renovate\\""},"lang__thank-title":{"ua":"Подяки для БФ \\"Заново\\"","en":"Thanks to Charity Fund \\"Renovate\\""}}');var o;o=JSON.parse('{"lang__donate-title":{"ua":"Допомогти","en":"Make a donation"},"lang__donate-nets-title":{"ua":"Підтримати постійний збір на маскувальні сітки","en":"Support the permanent collection of masking nets"},"lang__donate-nets-link-title":{"ua":"&#128279; Посилання на банку","en":"&#128279; Donation link"},"lang__donate-nets-credit-title":{"ua":"&#128179; Номер картки банки","en":"&#128179; Bank card number for donation"},"lang__donate-bank-title":{"ua":"Для банківського переказу:","en":"For bank transfer:"},"lang__donate-bank-details-title":{"ua":"(UAH) Українська гривня","en":"(UAH) Ukrainian hryvnia"}}');var r;r=JSON.parse('{"lang__zsu-page-title":{"ua":"БФ \\"ЗАНОВО\\". Допомога ЗСУ.","en":"RENOVATE CHARITY. Project \\"ZSU help\\""},"lang__zsu-title":{"ua":"Допомога ЗСУ","en":"Help to ZSU"},"lang__zsu-list-title-1":{"ua":"Повсякденні потреби","en":"Daily Needs"},"lang__zsu-list-description-1":{"ua":"Постійно підтримуємо наших військових на різних напрямках фронту для покращення їхнього буття та знаходимо для них все необхідне: забезпечуємо наших захисників продуктами харчування (тушонка, сухі типи каш і супів швидкого приготування, солодкі батончики та шоколад, консервовані продукти харчування), таблетками для знезараження води, засобами гігієни, термосами, пляшками для води, посудом, термобілизною, взуттям, спальниками, карематами, наметами, ліхтариками, павербанками, генераторами, акумуляторами, старлінками та іншим обладнанням, яке допомагає воїнам захищати нашу країну максимально ефективно.","en":"We constantly support our military in various areas of the front to improve their living conditions and find everything they need: providing our defenders with food (canned meat, instant cereals and soups, sweet bars and chocolate, canned food), water purification tablets, hygiene products, thermoses, water bottles, dishes, thermal underwear, shoes, sleeping bags, mats, tents, flashlights, power banks, generators, batteries, Starlinks, and other equipment to help them defend our country as effectively as possible."},"lang__zsu-list-title-2":{"ua":"Облаштування побуту","en":"Living Conditions"},"lang__zsu-list-description-2":{"ua":"Закуповуємо будівельні матеріали для покращення умов проживання наших воїнів в окопах. Завдяки плитам ДСП, оцинкованій сітці, скобам, цвяхам, саморізам, будівельним кріпленням, пилам, сокирам, лопатам та іншим буд. матеріалам, а також насосам для відкачування води наші захисники мають можливість добре облаштовувати свої бліндажі та підготуватись до будь-яких змін погоди.","en":"We purchase building materials to improve the living conditions of our soldiers in trenches. Thanks to chipboard, galvanized mesh, staples, nails, screws, building fasteners, saws, axes, shovels, and other building materials, as well as water pumps, our defenders can better equip their bunkers and prepare for any weather changes."},"lang__zsu-list-title-3":{"ua":"Забезпечення медициною","en":"Medical Supplies"},"lang__zsu-list-description-3":{"ua":"Особлива увага приділяється забезпеченню медициною наших воїнів: фіз. розчини, знеболювальні, етиловий спирт, адреналін, ізофілія, серцево-судинні та кишково-шлункові таблетки, вітаміни, зігріваючі гелі та мазі, антисептичні мазі та розчини. Крім роздрібної медицини також надаємо укомплектовані аптечки за військовим стандартом країн-донорів допомоги.","en":"Special attention is paid to providing our soldiers with medical supplies: saline solutions, painkillers, ethyl alcohol, adrenaline, isofilia, cardiovascular and gastrointestinal tablets, vitamins, warming gels and ointments, antiseptic ointments and solutions. In addition to retail medicine, we also provide fully equipped first aid kits according to military standards of donor countries."},"lang__zsu-list-title-4":{"ua":"Тактика та інше обладнання","en":"Tactical and Other Equipment"},"lang__zsu-list-description-4":{"ua":"Завдяки підтримці вітчизняних та закордонних організацій та приватних осіб нами закуплено та передано нашим військовим: дрони, рації, намети, термобілизну, лопати саперні, буржуйки, генератори бензинові, олива для генераторів, тепловізори, планшети, спальники, джерела безперебійного живлення, їжу, сухі душі, масло для зброї, обігрівачі, сонячні панелі та батареї до них, газові пальники.","en":"Thanks to the support of domestic and foreign organizations and private individuals, we have purchased and transferred to our military: drones, radios, tents, thermal underwear, sapper shovels, stoves, gasoline generators, generator oil, thermal imagers, tablets, sleeping bags, uninterruptible power supplies, food, dry showers, weapon oil, heaters, solar panels and batteries, gas burners."},"lang__zsu-list-title-5":{"ua":"Військовим медикам","en":"Military Medics"},"lang__zsu-list-description-5":{"ua":"Колаборація з вітчизняними фарм. компаніями, закордонними доброчинними організаціями та приватними особами дозволяє нам забезпечувати військових медиків на стабілізаційних пунктах та медичних евакуаційних екіпажах найнеобхіднішими медикаментами та виробами медичного призначення для збереження життів поранених: йоностеріл, гелафундін, транексам, дексаметазон, ондансетрон, лідокаїн, бупівакаїн, моксіфлоксацин, ЦВК різних розмірів, заглушки для в/в катетерів, тегадерм адванс в/в, канюлі міні спайк, вазофікси різних розмірів, дренажі торакальні, сечозбірники, набори для катетерізації сечового міхура, катетери сечові типу Фоллей, пов’язки окклюзійні, монітори пацієнта Mindray, набори для ПХО ран одноразові стерильні, стерильні рукавички, хірургічні голки, кисневі маски, польові ноші, кишеньковий міні-ехограф Wi-Fi.","en":"Collaboration with domestic pharmaceutical companies, foreign charitable organizations, and private individuals allows us to provide military medics at stabilization points and medical evacuation crews with the most necessary medications and medical devices to save the lives of the wounded: ionosteril, gelafundin, tranexam, dexamethasone, ondansetron, lidocaine, bupivacaine, moxifloxacin, CVCs of various sizes, IV catheter plugs, tegaderm advance IV, mini spike cannulas, vasofix of various sizes, thoracic drains, urine collectors, bladder catheterization kits, Foley catheters, occlusive dressings, Mindray patient monitors, single-use sterile wound care kits, sterile gloves, surgical needles, oxygen masks, field stretchers, pocket mini-ultrasound Wi-Fi."},"lang__zsu-list-title-6":{"ua":"На шпиталь","en":"For Hospitals"},"lang__zsu-list-description-6":{"ua":"Завдяки колаборації з українською мережею магазинів <span class=\\"text-uppercase\\">Humana</span>, нами передано пораненим у шпиталі та реабілітаційні центри: майки, футболки, сорочки, шорти, штани, кофти, светри, світшоти. Також невелику партію адаптивного одягу від компанії PHY нами було передано пораненим в шпиталь. Ще на військові шпиталі передаємо медицину: шприци та голки для ін’єкцій, шприци з фізрозчином, системи для змішування ліків для ін’єкцій, голки для забору крові, системи до крапельниць, зонди, комплектуючі для кало- та уріностом, ліки, мазі, медичні маски, медичний одяг, поїлки, дезінфектори, стерильні накладки на рани, бинти різного типу, накладки на очі і ще всілякі приспособи для хірургії та реанімації.","en":"Thanks to collaboration with the Ukrainian Humana store network, we have delivered to hospitals and rehabilitation centers for the wounded: t-shirts, shirts, shorts, pants, sweatshirts, sweaters. We also delivered a small batch of adaptive clothing from PHY to the hospital. Additionally, we deliver medical supplies to military hospitals: syringes and needles for injections, saline syringes, medicine mixing systems for injections, blood collection needles, IV systems, probes, components for colostomy and urostomy, medications, ointments, medical masks, medical clothing, drinkers, disinfectants, sterile wound pads, various types of bandages, eye pads, and various surgical and resuscitation aids."},"lang__zsu-list-title-7":{"ua":"Необхідне","en":"Needed Now"},"lang__zsu-list-description-7":{"ua":"Що зараз <span class=\\"text-uppercase\\">необхідне</span>, чим ще можна допомогти нашим воїнам? Необхідним і важливим є забезпечення поранених одягом. Коли поранений потрапляє на медичний стабілізаційний пункт, як правило, з нього зрізається вся його одежа. І що далі? Далі, після стабілізації бійця, його потрібно вдягнути в елементарні речі – білизну (в тому числі адаптивну), сорочки, штани, капці… Мати у що вдягнути пораненого – це про частину його життя, частину його відновлення, це про гідність та турботу, це про <span class=\\"text-uppercase\\">необхідне</span>. Тому ми розпочали цей проект і, разом з нашими друзями та партнерами – дівчатами з волонтерської групи Шити-Жити, шиємо білизну та забезпечуємо нею поранених на стабілізаційних пунктах та у шпиталях. Частково тканину для цього нам безкоштовно надає Алла Березовська, підприємець з м. Горішні Плавні.","en":"What do our soldiers <span class=\\"text-uppercase\\">need now</span>, and how else can we help them? It is crucial and important to provide the wounded with clothing. When a wounded soldier arrives at a medical stabilization point, all their clothing is usually cut off. What’s next? After stabilizing the fighter, they need to be dressed in basic items - underwear (including adaptive), shirts, pants, slippers... Having clothes for the wounded is part of their life, part of their recovery, it\'s about dignity and care, it\'s about <span class=\\"text-uppercase\\">necessity</span>. That’s why we started this project, and together with our friends and partners - the girls from the Shiti-Zhiti volunteer group, we sew underwear and provide it to the wounded at stabilization points and in hospitals. Part of the fabric for this is provided to us free of charge by Alla Berezovska, an entrepreneur from Horishni Plavni."},"lang__zsu-list-title-8":{"ua":"Адаптивний вже допомагає","en":"Adaptive Clothing Already Helping"},"lang__zsu-list-description-8":{"ua":"Адаптивний – специфічний одяг для поранених, що має липучки та зав\'язки замість швів і тому його зручно вдягати, носити, знімати та знову вдягати на перев\'язках. Це єдиний одяг, який легко можуть використовувати наші захисники зі складними пораненнями. Ми, разом з волонтерською групою Шити-Жити, забезпечуємо наших поранених адаптивним одягом: майками, футболками, шортами, штанами. Для цього ми організовуємо збори коштів, закуповуємо трикотажну тканину, а дівчата з Шити-Жити його шиють. Зрештою, наш адаптивний одяг дістається до стабілізаційних пунктів та шпиталів де допомагає воїнам.","en":"Adaptive clothing is specific clothing for the wounded that has velcro and ties instead of seams, making it easy to put on, wear, remove, and put on again during dressing changes. It is the only clothing that can be easily used by our defenders with complex wounds. Together with the Shiti-Zhiti volunteer group, we provide our wounded with adaptive clothing: t-shirts, shorts, pants. For this, we organize fundraisers, purchase knitwear, and the girls from Shiti-Zhiti sew it. Ultimately, our adaptive clothing reaches stabilization points and hospitals where it helps the warriors."}}');var l;l=JSON.parse('{"lang__reconstruction-page-title":{"ua":"БФ \\"ЗАНОВО\\". Відбудова країни.","en":"RENOVATE CHARITY. Project \\"Country Reconstruction\\""},"lang__reconstruction-title":{"ua":"Відбудова країни","en":"Country Reconstruction"},"lang__reconstruction-list-title-1":{"ua":"Відновлюємо інклюзивно-ресурсний центр у <br> смт Бородянка Київської області","en":"Restoring the Inclusive Resource Center <br> in Borodyanka, Kyiv Region"},"lang__reconstruction-list-description-1":{"ua":"(червень-липень 2023)","en":"(June-July 2023)"},"lang__reconstruction-description-1":{"ua":"У травні 2022 року ми відновили інклюзивно-ресурсний центр у Бородянці, який був пошкоджений внаслідок російських бомбардувань. Залучивши ресурси та волонтерів, ми відремонтували дах, стіни, стелю та замінили двері, щоб діти з особливими потребами знову мали доступ до необхідної допомоги.","en":"In May 2022, we restored the Inclusive Resource Center in Borodyanka, damaged by Russian bombings. We repaired the roof, walls, ceiling, and replaced the doors, allowing children with special needs to access necessary assistance again."},"lang__reconstruction-list-title-2":{"ua":"Відновлюємо паспортний стіл у смт Бородянка Київської області","en":"Restoring the Passport Office in Borodyanka, Kyiv Region"},"lang__reconstruction-list-description-2":{"ua":"(травень 2022)","en":"(May 2022)"},"lang__reconstruction-description-2":{"ua":"У травні 2022 року ми допомогли відновити паспортний стіл у Бородянці, який був пошкоджений під час бойових дій. Ми не тільки відремонтували приміщення, але й забезпечили його новими меблями та оргтехнікою, щоб громадяни могли швидко отримувати необхідні документи.","en":"In May 2022, we helped restore the passport office in Borodyanka, damaged during the fighting. We not only repaired the premises but also provided new furniture and office equipment to ensure citizens could quickly obtain necessary documents."},"lang__reconstruction-list-title-3":{"ua":"Відновлюємо дитячий садок \\"Казка\\" у смт Бородянка Київської області","en":"Restoring the \\"Kazka\\" Kindergarten in Borodyanka, Kyiv Region"},"lang__reconstruction-list-description-3":{"ua":"(червень-липень 2022)","en":"(June-July 2022)"},"lang__reconstruction-description-3":{"ua":"У червні-липні 2022 року ми відновили дитячий садок \\"Казка\\" у Бородянці, який був пошкоджений російськими окупантами. Завдяки благодійним внескам та волонтерській допомозі, ми замінили вибиті двері та вікна, щоб діти могли повернутися до безпечного і комфортного навчання.","en":"In June-July 2022, we restored the \\"Kazka\\" kindergarten in Borodyanka, damaged by Russian occupiers. Thanks to charitable donations and volunteer help, we replaced broken doors and windows, ensuring children could return to a safe and comfortable learning environment."},"lang__reconstruction-list-title-4":{"ua":"Відновлюємо середній заклад освіти школу-дитячий садок \\"Схід\\" у м. Києві","en":"Restoring the \\"Skhid\\" School-Kindergarten in Kyiv"},"lang__reconstruction-list-description-4":{"ua":"(з липня 2022)","en":"(from July 2022)"},"lang__reconstruction-description-4":{"ua":"З липня по листопад 2022 року ми відновлювали школу-дитячий садок \\"Схід\\" у Києві, яка постраждала від вибухової хвилі. Ми замінили вибиті вікна, щоб діти з особливими потребами могли продовжити своє навчання і розвиток у безпечних умовах. Завдяки вашій підтримці, ми створили три теплі кімнати та їдальню, де діти можуть займатися і відпочивати.","en":"From July to November 2022, we restored the \\"Skhid\\" school-kindergarten in Kyiv, damaged by an explosive wave. We replaced broken windows, ensuring children with special needs could continue their education and development in safe conditions. Thanks to your support, we created three warm rooms and a dining area where children can learn and relax."}}');var c;c=JSON.parse('{"lang__nets-page-title":{"ua":"БФ \\"ЗАНОВО\\". Маскувальні сітки.","en":"RENOVATE CHARITY. Project \\"Nets\\""},"lang__nets-title":{"ua":"Загін сітко-маскувальних сил","en":"Squad of Net-Camouflage Forces"},"lang__nets-description-1":{"ua":"\\"Загін Сітко-маскувальних сил\\" – окремий напрям нашого фонду. Починали ще на Київському центральному з-д вокзалі з простих сіток кольору \\"вся тканина, що у нас є\\", а тепер робимо професійні камуфляжні сітки, чохли для шоломів (кавери) та кікімори. Професійно підбираємо колір для маскувальних виробів відповідно порам року.","en":"\\"Squad of Net-Camouflage Forces\\" is a separate direction of our foundation. We started at the Kyiv central railway station with simple nets in the color of \\"all the fabric we have\\", and now we make professional camouflage nets, helmet covers (camos), and ghillie suits. We professionally select the color for camouflage products according to the seasons."},"lang__nets-description-2":{"ua":"З березня 2022 по січень 2024 року виготовили та передали нашим захисникам плетених сіток 126 штук, витинанок – 155 штук. Робота триває! Не зупиняємось! Плетемо до Перемоги!","en":"From March 2022 to January 2024, we made and delivered to our defenders 126 woven nets and 155 cut-out nets. The work continues! We weave until Victory!"},"lang__nets-description-3":{"ua":"Опанували спанбонд – диво матеріал для маскувальних сіток! Окрім звичайного плетіння на основі сітки, в яку вплітаються смужки тканини, задіяли спеціальний станок для пробивання отворів у спанбонді і почали виготовлення маскувальних сіток – витинанок. За рахунок такої оптимізації виробництва, суттєво збільшено обсяги готової продукції та скорочено час на її виготовлення.","en":"We have mastered spunbond - a miracle material for camouflage nets! Besides the usual weaving based on a grid into which fabric strips are woven, we have engaged a special machine for punching holes in spunbond and started making cut-out camouflage nets. This production optimization has significantly increased the volume of finished products and reduced the manufacturing time."},"lang__nets-description-4":{"ua":"З вересня 2022 по квітень 2024 тільки на станку виготовили зі спанбонду 20 000 м<sup>2</sup> маскувальних сіток-витинанок різного розміру! Не зупиняємось! Допомагаємо нашим воїнам бути максимально непомітними!","en":"From September 2022 to April 2024, we made 20,000 m<sup>2</sup> of spunbond cut-out camouflage nets of various sizes using the machine! We don\'t stop! We help our warriors remain as invisible as possible!"},"lang__nets-description-5":{"ua":"Наш пречудовий Загін сітко-маскувальних сил робить дива: плете велетенські сітки! Це цілий тривалий процес. І найскладніше в ньому – це зшивання всіх частин в одне величезне ціле. Але для наших неймовірних волонтерів немає нічого неможливого!","en":"Our wonderful Squad of Net-Camouflage Forces creates giant nets! This is a long process, and the most challenging part is sewing all the parts into one huge whole. But nothing is impossible for our incredible volunteers!"},"lang__nets-description-6":{"ua":"З вересня 2022 по січень 2024 виготовлено чотири велетенські маскувальні сітки-витинанки 20x20 м<sup>2</sup>; п’ять сіток 12x6 м<sup>2</sup> (з них: 3 витинанки, 2 плетені); одна сітка плетена 11x6 м<sup>2</sup>; дві витинанки 10x2 м<sup>2</sup> та багато сіток 10x10 м<sup>2</sup>, як витинанок, так і плетених. Робота триває! Плетемо до Перемоги!","en":"From September 2022 to January 2024, we made four giant cut-out nets of 20x20 m<sup>2</sup>; five nets of 12x6 m<sup>2</sup> (three cut-out, two woven); one woven net of 11x6 m<sup>2</sup>; two cut-out nets of 10x2 m<sup>2</sup>; and many nets of 10x10 m<sup>2</sup>, both cut-out and woven. The work continues! We weave until Victory!"},"lang__nets-description-7":{"ua":"Що таке кікімора? Це супермаскування для розвідників, снайперів і всіх тих, кому потрібно бути максимально непомітним та ефективним у знищенні ворога. Наш Загін сітко-маскувальних сил виробляє найнепомітніші кікімори, які до того ж дуже зносостійкі та зручні у використанні.","en":"What is a ghillie suit? It is super-camouflage for scouts, snipers, and anyone who needs to be as invisible and effective as possible in eliminating the enemy. Our Squad of Net-Camouflage Forces makes the most inconspicuous ghillie suits, which are also very durable and easy to use."},"lang__nets-description-8":{"ua":"І в цьому нам допомагають супер-дівчата з магазину \\"Швейний світ\\" (Київ). Вони шиють якісні основи для кікімор, постійно вдосконалюючи їх моделі. Ми дуже цінуємо нашу з ними дружбу та співробітництво! Дякуємо що йдемо до Перемоги разом! До січня 2024 року вже виготовлено та передано нашим захисникам 302 штуки кікімор. Робота триває! Не зупиняємось!","en":"Super-girls from the \\"Sewing World\\" store (Kyiv) help us in this. They sew quality bases for ghillie suits, constantly improving their models. We highly value our friendship and cooperation with them! Thank you for moving towards Victory together! As of January 2024, 302 ghillie suits have been made and delivered to our defenders. The work continues! We don\'t stop!"},"lang__nets-description-9":{"ua":"Ще один вид маскування: чохли (кавери), які надягаються на шоломи військових та допомагають воїнам бути непомітними для ворога.","en":"Another type of camouflage: covers (camos) worn over military helmets to help soldiers remain invisible to the enemy."},"lang__nets-description-10":{"ua":"До січня 2024 року вже виготовлено та передано нашим захисникам 676 штук таких каверів. Не зупиняємось! Продовжуємо забезпечувати наших захисників необхідним маскуванням!","en":"As of January 2024, 676 covers have been made and delivered to our defenders. We don\'t stop! We continue to provide our defenders with essential camouflage!"},"lang__nets-presentation-1":{"ua":"Представляємо нашу презентацію \\"Маскувальні сітки зі спанбонду лазерною порізкою для ЗСУ\\", яка розповідає що таке спанбонд та про основні етапи виготовлення маскувальних сіток-витинанок.","en":"We present our presentation \\"Spunbond Laser-Cut Camouflage Nets for the Armed Forces of Ukraine\\", which explains what spunbond is and the main stages of making cut-out camouflage nets."},"lang__nets-presentation-2":{"ua":"Подивитись презентацію","en":"View Presentation"}}');const u=["ua","en"];let p=localStorage.getItem("language")||function(){const e=navigator.language.slice(0,2).toLowerCase();if(u.some((a=>a===e)))return e}()||"ua";const d=document.querySelectorAll("[data-btn]"),h=window.location.pathname;let _={};function g(){for(const e in _){const a=document.querySelector(`[data-lang=${e}]`);a&&(a.innerHTML=_[e][p])}}!function(){switch(h){case"/index.html":default:_=Object.assign(e(n),e(a),e(t));break;case"/about-us.html":_=Object.assign(e(s),e(a),e(t));break;case"/fund-report.html":_=Object.assign(e(i),e(a),e(t));break;case"/donate.html":_=Object.assign(e(o),e(a),e(t));break;case"/unit-zsu-help.html":_=Object.assign(e(r),e(a),e(t));break;case"/unit-reconstruction.html":_=Object.assign(e(l),e(a),e(t));break;case"/unit-nets.html":_=Object.assign(e(c),e(a),e(t))}}(),g(),d.forEach((e=>{e.addEventListener("click",(a=>{var t;p=a.target.dataset.btn,localStorage.setItem("language",p),t="header__lang-btn-active",d.forEach((e=>{e.classList.remove(t)})),e.classList.add("header__lang-btn-active"),g()}))})),function(){let e;switch(p){case"ua":default:e=document.querySelector('[data-btn="ua"]');break;case"en":e=document.querySelector('[data-btn="en"]')}e?e.classList.add("header__lang-btn-active"):console.error(`Element with data-btn="${p}" not found.`)}();
